import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import './index.css';


class compsGitHub extends React.Component {
  state = {
    comps: [],
    aviso: ''
  }

 async setComps(){
  	const list = await getListComponents();
  	this.setState({comps: list});
  }

 async getListComponents(){
  	
  	try{
  		const res = axios.get('https://kctbh9vrtdwd.statuspage.io/api/v2/components.json');
  		
  		return res;
  	} catch (e){
  		console.log("Erro ao realizar requisição! " + e);
  	}
  }

  componentDidMount(){
  	this.setComps();
  }


//
  render() {
  	return(
  		<div>
  			<h1>GitHub Status</h1>
	        { this.state.comps.map(comp => 
	        	<div className='_1WX-ZNh1u_6_qUFuQYrWF' key={comp.id}> 
					{comp.name} | { comp.status == 'operational' ? 'Funcionando' : 'Fora do ar'} 
	        	</div>	
	        )}

	        <button onClick={this.setComps()}>Atualizar</button>
      	</div>
    	)
  	}
}


export default compsGitHub;
