import React from 'react';

import axios from 'axios';

export default class compsGitHub extends React.Component {
  state = {
    comps: []
  }

  componentDidMount() {
    axios.get('https://kctbh9vrtdwd.statuspage.io/api/v2/summary.json')
      .then(res => {
        const comps = res.data.components;
        this.setState({ comps });
        console.log(comps);
      })
  }

  render() {
    return (
      <ul>
        { this.state.comps.map(comp => <li>{comp.name} | {comp.status}</li>)}
      </ul>
    )
  }
}
