import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import './index.css';


class compsGitHub extends React.Component {
  state = {
    comps: [],
    aviso: ''
  }

  getComps(){
  	axios.get('https://kctbh9vrtdwd.statuspage.io/api/v2/components.json')
      .then(res => {
      	console.log(res.data.components);
        this.setState({ comps: res.data.components });
      })
      .catch(e => {
      	console.log("Não foi possivel carregar o conteudo! Atualize a página! " + e);
      })
  }

  componentWillMount(){
  	this.getComps();
  }
//{comp.name} | { comp.status == 'operational' ? 'Funcionando' : 'Fora do ar'} 
  render() {
  	return(
  		<div>
  			<h1>GitHub Status</h1>
	        { this.state.comps.map(comp => 
	        	<div className='_1WX-ZNh1u_6_qUFuQYrWF' key={comp.id}> 
					<compsStatus status="comp.status" name="comp.name"/>
	        	</div>	
	        )}

	        <button onClick={this.getComps()}>Atualizar</button>
      	</div>
    	)
  	}
}

class compsStatus extends React.Component{

	render(){
		let statusC = props.status;
		let compGit = props.name;
		let text = '';

		if(statusC == 'operational'){
			statusC = 'Funcionando';
			text = statusC;
		}
		else{
			statusC = 'Fora do ar';
			text = statusC + ' - Motivo: ' + this.props.status;
		}
		return(

			<p>text</p>

		)
	}
}

export default compsGitHub;
