import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import './index.css';


class compsGitHub extends React.Component {
  state = {
    comps: [],
    aviso: ''
  }

 async getComps(){
  	
  	try{
  		const res = await axios.get('htt://kctbh9vrtdwd.statuspage.io/api/v2/components.json');
  		this.setState({comps: res.data.components});
  		console.log('tei');
  	} catch (e){
  		console.log("Erro ao realizar requisição!");
  	}
  }

  componentDidMount(){
  	this.getComps();
  }


//
  render() {
  	return(
  		<div>
  			<h1>GitHub Status</h1>
	        { this.state.comps.map(comp => 
	        	<div className='_1WX-ZNh1u_6_qUFuQYrWF' key={comp.id}> 
					{comp.name} | { comp.status == 'operational' ? 'Funcionando' : 'Fora do ar'} 
	        	</div>	
	        )}

	        <button onClick={this.componentDidMount()}>Atualizar</button>
      	</div>
    	)
  	}
}


export default compsGitHub;
