import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import './index.css';

class compsGitHub extends React.Component {
  state = {
    comps: []
  }

  getComps(){
  	axios.get('https://kctbh9vrtdwd.statuspage.io/api/v2/components.json')
      .then(res => {
        this.setState({ comps: res.data.components });
        //console.log(comps);
      })
      .catch(e => {
      	console.log("Erro tente novamente!" + e);
      })
  }

  componentDidMount() {
    this.getComps();
  }

  render() {
  	return(
  		<div>
	        { this.state.comps.map(comp => <div className={'comp'}> {comp.name} | {comp.status} </div>)}
	        <button onClick={this.getComps}>Atualizar</button>
      	</div>
    	)
  	}
}

export default compsGitHub;
