import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
export default class compsGitHub extends React.Component {
  state = {
    comps: []
  }

  componentDidMount() {
    axios.get('https://kctbh9vrtdwd.statuspage.io/api/v2/components.json')
      .then(res => {
        const comps = res.data.components;
        this.setState({ comps });
        console.log(comps);
      })
  }

  render() {
  	return(
  		<div>
	      	<ul>
	        	{ this.state.comps.map(comp => <li>{comp.name} | {comp.status}</li>)}
	      	</ul>
      	</div>
    )
  }
}
